import { useEffect, useState } from "react";


function App() {
  const [addproduct, setAddproduct] = useState();
  const [addnewproduct, setAddnewproduct] = useState(['']);

  // useEffect(()=>{
  //   console.log(fname)
  // },[fname])

  const submitform = () => {
    if (addproduct.trim() !== '') {

      setAddnewproduct([...addproduct, addnewproduct]);
      setAddproduct('');

    }

  };
  return (
    <div className="App">
    <h1>Details_of Product_Form</h1>

        <input type="text" onChange={(e) => setAddproduct(e.target.value)} placeholder="Enter the product :-" />

        <button onClick={submitform}>Submit Data</button>


      {/* <button onClick={() => {
        setAddnewproduct((prevState) => {
          return [...prevState, addproduct];
        })
      }}> Click Me</button> */}


      <ul>
      {
      addnewproduct.map((index)=>{
        return(
          <>
          <li>{index}</li>
        </>
        )
       }
       )}
      </ul>



    </div>
  );
}

export default App;

