import { useEffect, useState } from "react";


function App() {
  const [addproduct, setAddproduct] = useState();
  const [addnewproduct, setAddnewproduct] = useState(['']);

  // useEffect(()=>{
  //   console.log(fname)
  // },[fname])

  const submitform = (e) => {
    if (addproduct.trim !== '') {

      setAddnewproduct([...addproduct, addnewproduct]);
      setAddproduct('');

    }

  }
  return (
    <div className="App">

      <form action="#" onSubmit={submitform}>
        <h1>Details_of Product_Form</h1>

        <input type="text" onChange={(e) => setAddproduct(e.target.value)} placeholder="Enter the product :-" />
        <br></br>
        <button onSubmit={submitform}>Submit Data</button>

      <ul>
      {
      addnewproduct.map((index)=>{
        return(
          <>
        <li>{index}</li>
        </>
        )
       }
       )}
      </ul>

      </form>

    </div>
  );
}

export default App;

